$(document).ready(function(){

	// 백년대학
	if($(".univ").length) {

		var slick_cnt_news = 0;
		var slick_cnt_ques = 0;

		$(".latest_wrap > ul > li a.show_tab").click(function(e) {
			e.preventDefault();
			$(".latest_wrap > ul > li > p").removeClass("active");
			$(this).parent('p').addClass("active");
			$(".latest_wrap > ul > li > ul").hide();

			if($( window ).width() < 464) {
				$('.more_btn').hide();
				$(this).next('.more_btn').show();
			}else{
				$(".more_btn").show();
			}

			var activeTab = $(this).data("show");

			$("#"+activeTab).fadeIn();

			if(activeTab == 'news'){
				if(slick_cnt_news == 0) {
					$("#news").slick({
						slidesToShow: 4,
						slidesToScroll: 1,
						dots: false,
						autoplay: true,
						autoplaySpeed: 3500,
						centerPadding: 0,
						arrow: false,
						responsive: [
							{
								breakpoint: 1620,
								settings: {
									slidesToShow: 3,
									slidesToScroll: 1,
								}
							},

							{
								breakpoint: 650,
								settings: {
									slidesToShow: 2,
									slidesToScroll: 1,
								}
							}
						]
					});
				}
				slick_cnt_news++;
			}

			if(activeTab == 'question'){
				if(slick_cnt_ques == 0) {
					$("#question").slick({
						slidesToShow: 4,
						slidesToScroll: 1,
						dots: false,
						autoplay: true,
						autoplaySpeed: 3500,
						centerPadding: 0,
						arrow: false,
						responsive: [
							{
								breakpoint: 1620,
								settings: {
									slidesToShow: 3,
									slidesToScroll: 1,
								}
							},

							{
								breakpoint: 650,
								settings: {
									slidesToShow: 2,
									slidesToScroll: 1,
								}
							}
						]
					});
				}
				slick_cnt_ques++;

			}

		   return false;
		});
	}

	// 평생교육
	$(".gall_title > ul > li > a").click(function(e) {
		if($(".menu_pys").parent('li').hasClass("active")){
			e.preventDefault();
			$(".gall_title > ul > li").removeClass("active");
			$(this).parent('li').addClass("active");
			$(".gall_wrap .gall_list").hide();

			var activeTab = $(this).data("show");

			$("#"+activeTab).fadeIn();

			var img_chagne_oth = $(this).find("img").attr("src").split("plus").join("actv");
			$(".gall_title > ul > li > a").find("img").attr("src",img_chagne_oth);
			var img_chagnes = $(this).find("img").attr("src").split("actv").join("plus");
			$(this).find("img").attr("src",img_chagnes);

			return false;
		}
	});
});




